import { useRecoilState, useRecoilValue, useSetRecoilState } from "recoil"
import { calorieCountState } from "./atom"

export const useCalculus= () => {

    const setCalorieCount=useRecoilState(calorieCountState)

    return(weight:string,height:number,age:number,gender:number,activityLevel:number,)=>{
        return setCalorieCount[(10*parseFloat(weight))+(6.25*height)-(5*age)+(gender===1?-161:5)]
    }
}
