import { useSetRecoilState } from "recoil"
import { idealWeightState } from "./atom"

export const useIdealWeightCalculator= () => {

    const setIdealWeight=useSetRecoilState(idealWeightState)

    return(weight:string,gender:number)=>{
        let weightVal = parseInt(weight)/100
        let result = 0;

        if(gender===1){
            result=22*((weightVal-0.1)*(weightVal-0.1))
        }else{
            result=22*(weightVal^2)
        }

        setIdealWeight(result)
        return result
    }
}
