import { useSetRecoilState } from "recoil"
import { calorieCountState } from "./atom"
import { useCalorieCountValue } from "./useResult"

export const useCalculus= () => {

    const setCalorieCount=useSetRecoilState(calorieCountState)
    const results = useCalorieCountValue()

    return(weight:string,height:number,age:number,gender:number,activityLevel:number,)=>{
        setCalorieCount((10*parseFloat(weight))+(6.25*height)-(5*age)+(gender===1?-161:5))
        return results
    }
}
